def chunks(l, n):
    """Yield successive n-sized chunks from l."""
    for i in range(0, len(l), n):
        yield l[i:i + n]


def decrypt_obfuscated_js(obfuscated_javascript):
    """
    Parse out the obfuscated javascript code which is normally contained in the incapsula iframe.

    :param obfuscated_javascript:
    :return:
    """
    return ''.join([chr(int(x, 16)) for x in chunks(obfuscated_javascript, 2)])


if __name__ == '__main__':
    print(decrypt_obfuscated_js(''))
